$(document).ready(function(){

  function fadeAlert(){
    $('.alert-temp').removeClass('in');
  }

  function removeAlert(){
    $('.alert-temp').remove();
  }

  window.setTimeout(fadeAlert,3000);
  window.setTimeout(removeAlert,3000);

  // Popover Management
  $(function (){
    $("[data-toggle='popover']").popover({html:true});
  });


  $('.dropdown-toggle').dropdown();
});
